// Dashboard.tsx
import React, { useState, useEffect } from "react";
import Filters from "./Filters";
import MovieList from "./MovieList";
import TopStudiosSection from "./Dashboard/getTopStudios";
import { getWinnersByYear, getProducerIntervals } from "../services/api"; // Remova a importação de getTopStudios

const Dashboard: React.FC = () => {
  const [winnersByYear, setWinnersByYear] = useState([]);
  const [producerIntervals, setProducerIntervals] = useState([]);

  useEffect(() => {
    async function fetchData() {
      const winnersByYearData = await getWinnersByYear();
      const producerIntervalsData = await getProducerIntervals();

      setWinnersByYear(winnersByYearData);
      setProducerIntervals(producerIntervalsData);
    }

    fetchData();
  }, []);

  return (
    <div>
      <h1>Dashboard</h1>
      <Filters />
      <h2>Years with more than one winner</h2>
      <table>
        <thead>
          <tr>
            <th>Year</th>
          </tr>
        </thead>
        <tbody>
          {winnersByYear.map((year: string) => (
            <tr key={year}>
              <td>{year}</td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Inclua o componente TopStudiosSection aqui */}
      <TopStudiosSection />

      <h2>Producer Intervals</h2>
      <table>
        <thead>
          <tr>
            <th>Producer</th>
            <th>Interval</th>
          </tr>
        </thead>
        <tbody>
          {producerIntervals.map(
            (producer: { name: string; interval: number }) => (
              <tr key={producer.name}>
                <td>{producer.name}</td>
                <td>{producer.interval}</td>
              </tr>
            )
          )}
        </tbody>
      </table>

      {/* Add table for winners of a specific year here */}
    </div>
  );
};

export default Dashboard;
