import React, { useEffect, useState } from "react";
import { getMovies } from "../services/api";
import "./MovieList.css";

const MoviesList: React.FC = () => {
  const [movies, setMovies] = useState<any[]>([]);
  const [page, setPage] = useState<number>(1);
  const [totalPages, setTotalPages] = useState<number>(1);
  const [yearFilter, setYearFilter] = useState<string>("");
  const [winnerFilter, setWinnerFilter] = useState<string>("");

  useEffect(() => {
    fetchMovies(page, yearFilter);
  }, [page, yearFilter]);

  const fetchMovies = async (pageNumber: number, year?: string) => {
    try {
      const response = await getMovies(pageNumber, 15, year || undefined);
      setMovies(response.content);
      setTotalPages(response.totalPages - 1);
    } catch (error) {
      console.error("Error fetching movies:", error);
    }
  };

  const handleNextPage = () => {
    setPage(page + 1);
  };

  const handlePreviousPage = () => {
    setPage(page - 1);
  };

  const handleFirstPage = () => {
    setPage(1);
  };

  const handleLastPage = () => {
    setPage(totalPages);
  };

  const handleYearChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setYearFilter(event.target.value);
  };

  const handleWinnerChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setWinnerFilter(event.target.value);
  };

  const handleSearch = () => {
    setPage(1);
  };

  return (
    <div className="winning-movies-container">
      <h2>Movies List</h2>
      <div className="search-form">
        <input
          type="text"
          placeholder="Enter year"
          value={yearFilter}
          onChange={handleYearChange}
          className="year-input"
        />
        <input
          type="text"
          placeholder="Enter winner"
          value={winnerFilter}
          onChange={handleWinnerChange}
          className="winner-input"
        />
        <button onClick={handleSearch} className="search-button">
          Search
        </button>
      </div>
      <table className="winning-movies-table">
        <thead className="table-header">
          <tr>
            <th>ID</th>
            <th>Year</th>
            <th>Title</th>
            <th>Winner</th>
          </tr>
        </thead>
        <tbody className="table-content">
          {movies.map((movie) => (
            <tr key={movie.id}>
              <td>{movie.id}</td>
              <td>{movie.year}</td>
              <td>{movie.title}</td>
              <td>{movie.winner ? "Yes" : "No"}</td>
            </tr>
          ))}
        </tbody>
      </table>
      <div className="pagination">
        <button onClick={handleFirstPage} disabled={page === 1}>
          Início
        </button>
        <button onClick={handlePreviousPage} disabled={page === 1}>
          Anterior
        </button>
        <span>{`Page ${page} of ${totalPages}`}</span>
        <button onClick={handleNextPage} disabled={page === totalPages}>
          Próximo
        </button>
        <button onClick={handleLastPage} disabled={page === totalPages}>
          Último
        </button>
      </div>
    </div>
  );
};

export default MoviesList;
