import React, { useState, useEffect } from "react";
import Filters from "./Filters";
import MovieList from "./MovieList";
import {
  getWinnersByYear,
  getTopStudios,
  getProducerIntervals,
} from "../services/api";

const Dashboard: React.FC = () => {
  const [winnersByYear, setWinnersByYear] = useState([]);
  const [topStudios, setTopStudios] = useState([]);
  const [producerIntervals, setProducerIntervals] = useState([]);

  useEffect(() => {
    async function fetchData() {
      const winnersByYearData = await getWinnersByYear();
      const topStudiosData = await getTopStudios();
      const producerIntervalsData = await getProducerIntervals();

      setWinnersByYear(winnersByYearData);
      setTopStudios(topStudiosData);
      setProducerIntervals(producerIntervalsData);
    }

    fetchData();
  }, []);

  return (
    <div>
      <h1>Dashboard</h1>
      <Filters />
      <h2>Years with more than one winner</h2>
      <table>
        <thead>
          <tr>
            <th>Year</th>
          </tr>
        </thead>
        <tbody>
          {winnersByYear.map((year: string) => (
            <tr key={year}>
              <td>{year}</td>
            </tr>
          ))}
        </tbody>
      </table>

      <h2>Top Studios</h2>
      <table>
        <thead>
          <tr>
            <th>Studio</th>
            <th>Wins</th>
          </tr>
        </thead>
        <tbody>
          {topStudios.map((studio: { name: string; wins: number }) => (
            <tr key={studio.name}>
              <td>{studio.name}</td>
              <td>{studio.wins}</td>
            </tr>
          ))}
        </tbody>
      </table>

      <h2>Producer Intervals</h2>
      <table>
        <thead>
          <tr>
            <th>Producer</th>
            <th>Interval</th>
          </tr>
        </thead>
        <tbody>
          {producerIntervals.map(
            (producer: { name: string; interval: number }) => (
              <tr key={producer.name}>
                <td>{producer.name}</td>
                <td>{producer.interval}</td>
              </tr>
            )
          )}
        </tbody>
      </table>

      {/* Add table for winners of a specific year here */}
    </div>
  );
};

export default Dashboard;
