// MovieList.tsx

import React, { useEffect, useState } from "react";
import { getMovies } from "../services/api";
import "./MovieList.css";

const MovieList: React.FC = () => {
  const [movies, setMovies] = useState<any[]>([]);
  const [page, setPage] = useState<number>(1);
  const [totalPages, setTotalPages] = useState<number>(1);
  const [searchYear, setSearchYear] = useState<string>("");

  useEffect(() => {
    fetchMovies(page, searchYear);
  }, [page, searchYear]);

  const fetchMovies = async (pageNumber: number, year: string) => {
    try {
      const response = await getMovies(pageNumber, 15, year);
      setMovies(response.content);
      setTotalPages(response.totalPages);
    } catch (error) {
      console.error("Error fetching movies:", error);
    }
  };

  const handleNextPage = () => {
    setPage(page + 1);
  };

  const handlePreviousPage = () => {
    setPage(page - 1);
  };

  const handleSearchYearChange = (
    event: React.ChangeEvent<HTMLInputElement>
  ) => {
    setSearchYear(event.target.value);
  };

  const handleSearch = () => {
    fetchMovies(page, searchYear);
  };

  return (
    <div className="winning-movies-container">
      <h2>Movies List</h2>
      <div className="search-form">
        <label>
          Search Year:
          <input
            type="text"
            value={searchYear}
            onChange={handleSearchYearChange}
            className="year-input"
          />
        </label>
        <button onClick={handleSearch} className="search-button">
          Search
        </button>
      </div>
      <table className="winning-movies-table">
        <thead className="table-header">
          <tr>
            <th>ID</th>
            <th>Year</th>
            <th>Title</th>
            <th>Winner</th>
          </tr>
        </thead>
        <tbody className="table-content">
          {movies.map((movie) => (
            <tr key={movie.id}>
              <td>{movie.id}</td>
              <td>{movie.year}</td>
              <td>{movie.title}</td>
              <td>{movie.winner ? "Yes" : "No"}</td>
            </tr>
          ))}
        </tbody>
      </table>
      <div className="pagination">
        <button onClick={handlePreviousPage} disabled={page === 1}>
          Previous
        </button>
        <span>{`Page ${page} of ${totalPages}`}</span>
        <button onClick={handleNextPage} disabled={page === totalPages}>
          Next
        </button>
      </div>
    </div>
  );
};

export default MovieList;
